CREATE DATABASE IF NOT EXISTS `catrobat`;

USE `catrobat`;

CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `headline` text NOT NULL,
	`date` datetime,
  `text` text NOT NULL,
  `image` text,
  `link` text,
  PRIMARY KEY (id)
);

INSERT INTO `news` VALUES (1,'Educators\' platform launched!','2016-01-30 00:00:00','Find, contribute, and share curricular material and tutorials about how Pocket Code can be used in schools!','educatrobat.jpg','http://edu.catrob.at/'),(2,'Be part of the #AliceGameJam with Pocket Code and Scratch!','2015-12-07 00:00:00','Participate in the Alice Game Jame with Pocket Code and Scratch during the Computer Science Education week from 12 7th to 13th! Create and design your own Alice themed game or story and share it with us!<br/>All information needed and some cool tutorial and media-stuff can be found on the special event website!<br/>Let\'s code and be part of something big!','AGJ_HoC.jpg','http://www.alicegamejam.com'),(3,'Google supports Pocket Code to teach children how to code','2015-11-19 00:00:00','Fundings by Google help us to reach out for children all over the world and give them the chance to learn how to code with Pocket Code! Schools and teenagers worldwide are already using Pocket Code and create their own apps and games, to get prepared for the digital future. Thanks to Google we even can reach out for more children and provide them additional materials and tutorials!','PC_worldwide.jpg','https://growthengine.withgoogle.com/intl/en-eu/voices#card-story/5452329046245376'),(4,'Pocket Code featured at the Lovie Letters','2015-11-02 00:00:00','Find Pocket Code on the Lovie Letters presented by Google!<br />Interesting facts about the project, its history and the people behind it!','lovie_letters.jpg','http://www.lovieletters.eu/#/projects/68'),(5,'Pocket Code supports Phiro robot, a staff pick on kickstarter.com!','2015-11-02 00:00:00','The Phiro Pro robot can be controlled entirely through Pocket Code!<br /><b>UPDATE:</b> Funding goal achieved after only five days, with 32 left to go!','PhiroKickstarter.jpg','http://catrob.at/PhiroKickstarter'),(6,'Pocket Code and the No One Left Behind project won the ICT 2015 \'Young Minds\' Award - Grand Prix Best Connect Exhibitor Award!','2015-10-26 00:00:00','Together with the Human Brain project, we won the prize for the best exhibitor at the Horizon2020 conference of the European Commission!','ict2015.jpg','http://catrob.at/ict2015bestexhibitor'),(7,'Pocket Code won a Lovie Award and a People\'s Lovie Award!','2015-10-13 00:00:00','Pocket Code has been named a Silver Winner of the Lovie Awards and a winner of the People\'s Lovie Awards.<br>Thank you for your votes and support!','lovies2015_winner.jpg','http://winners.lovieawards.eu/'),(8,'Be part of the Alice Game Jam during #CodeWeekEU!','2015-10-10 00:00:00','Create your own Alice themed Game, App or Story ?with Pocket Code!','AliceGameJamEU.jpg','http://www.alicegamejam.eu/'),(9,'Pocket Code now has a media library!','2015-09-21 00:00:00','Please consider creating and contributing images and sounds!','medialibrary.jpg','https://plus.google.com/+WolfgangSlany/posts/Anv1NtJAnNz'),(10,'We are looking for translators!','2015-09-05 00:00:00','Please help us to reach out to even more kids by translating our apps to your language!','localization.jpg','https://plus.google.com/+WolfgangSlany/posts/ZW9h2ndnq9M'),(11,'Pocket Code featured in Google\'s official \"Tech Tips from Teachers\" YouTube channel!','2015-08-04 00:00:00','The kids featured in the video are from our partner school in Graz, Austria, near our headquarters!','TechTipsFromTeachers.jpg','http://catrob.at/gttft'),(12,'Pocket Code fully supports Lego Mindstorms NXT robot programming!','2015-07-15 00:00:00','You only need your Android smartphone to give your robot a face, a voice, and computer vision!','LegoMindstormsNXT.jpg','https://plus.google.com/+WolfgangSlany/posts/4xsTm2tyrM4'),(13,'Pocket Code now allows to create real Android apps!','2015-06-12 00:00:00','You can download your Catrobat program after uploading it first to the sharing site!','DownloadAsApp.jpg','https://plus.google.com/+WolfgangSlany/posts/huafmHDWvh1'),(14,'Tatiyana Domanova is awarded Google Anita Borg Memorial Scholarship!','2015-05-22 00:00:00','Catrobat team member and former Google Summer of Code student receives prestigious scholarship from Google!','AnitaBorg.jpg','http://www.google.co.uk/anitaborg/emea/index.html'),(15,'No One Left Behind research project funded by European Union!','2015-05-19 00:00:00','Centers on Pocket Code for game based learning, focuses on inclusion!','NOLB.jpg','http://catrob.at/nolb'),(16,'Catrobat is awarded highly competitive CS4HS grant by Google!','2015-04-29 00:00:00','Will be used to create a massive open online course (MOOC) on Pocket Code!<br/><b>UPDATE</b> (8 11 2015): First parts are available on https://edu.catrob.at/','cs4hs.jpg','http://catrob.at/cs4hs'),(17,'Catrobat article on Google\'s Open Source blog!','2015-02-13 00:00:00','Our Google Summer of Code 2014 wrap-up is featured on Google\'s Open Source blog!','google-summer-of-code-2014-logo.jpg','http://google-opensource.blogspot.com/2015/02/google-summer-of-code-wrap-up-catrobat.html'),(18,'Pocket Code at the Global Games Jam 2015!','0000-00-00 00:00:00','Catrobat took part at GGJ\'2015 held at GameCity in Nottingham!','ggj2015.jpg','https://plus.google.com/111699067405204652905/posts/V7sjhudPCVK'),(19,'Beyond One Hour of Code!','2014-12-09 00:00:00','Pocket Code is featured on code.org/learn/beyond under \"Tutorial apps for phones and tablets\"!','hourofcode2014.jpg','http://code.org/learn/beyond'),(20,'Programmieren am Handy nach dem Legoprinzip!','2014-11-26 00:00:00','Newspaper article on Catrobat (in German language)!','DerStandard_20141126.jpg','http://catrob.at/20141126'),(21,'Create an Android game in less than one hour!','2014-10-11 00:00:00','Pocket Code is featured during the European Code Week!','Hour-of-code-promotion_small_2.jpg','https://pocketcode.org/hourOfCode'),(22,'Take a crash course in fun!','2014-09-04 00:00:00','Pocket Code featured by Google in back-to-school promotion!','Google_play_learning_start_here_20140829.jpg','http://catrob.at/gpgbtspc'),(23,'Invited talk about Pocket Code','2014-08-22 00:00:00','Forthcoming features presented at the Constructionism conference','constructionism.jpg','http://youtu.be/BadcMBS5WgM'),(24,'Scratch team visits Catrobat HQ to start collaboration','2014-08-18 00:00:00','Mitch Resnick gave a talk on Sowing the seeds for a more creative society','scratch.jpg','https://plus.google.com/102627966522470894143/posts/Drd5oi4mK5c'),(25,'First Android App creation contest with Pocket Code','2014-07-07 00:00:00','The winning app will be converted into an Android app on Google Play!','www_pocketcode_org.jpg','https://plus.google.com/102627966522470894143/posts/F95q7a1oNUY'),(26,'Worldwide the first app on Google Play created entirely <em>on</em> a smartphone','2014-05-08 00:00:00','Tic-Tac-Toe Master was downloaded 23,000 times from Google Play so far!','tic-tac-toe-master.jpg','http://catrob.at/gptttm'),(27,'Pocket Code and Pocket Paint on Google Play for Education','2014-03-09 00:00:00','Video of our apps used in schools for STEM education!','gpfe_cover_small.jpg','http://youtu.be/75i10o_uv0U'),(28,'New versions of our Apps on Google Play','2014-03-08 00:00:00','Pocket Code and Pocket Paint are out of the beta and freely available on Google Play!','pocketcode_pocketpaint_icons.jpg','http://goo.gl/3hezq0'),(29,'Google Summer of Code 2014','2014-02-24 00:00:00','Google selected us for the fourth time as a Google Summer of Code mentoring organization!','GoogleSummer_2014logo_avatar.jpg','http://www.google-melange.com/gsoc/org2/google/gsoc2014/catrobat'),(30,'Catrobat at the Computer Science Education Week','2013-12-09 00:00:00','Pocket Code is participating at the Computer Science Education Week!','hourofcode.jpg','http://goo.gl/E5GYT0'),(31,'Catrobat is participating at the European Code Week','2013-11-29 00:00:00','Pocket Code is demonstrated at the European Code Week and Robotics Day!','codeEU-logo.jpg','http://goo.gl/RRFOcK'),(32,'Google is promoting Pocket Code','2013-11-14 00:00:00','Google is promoting Pocket Code heavily during the launch of Google Play for Education!','gp-edu-apps-n7.jpg','http://goo.gl/djpvjT'),(33,'Live AR.Drone programming at Scratch\'2013','2013-07-25 00:00:00','Pocket Code presentation at Scratch\'2013, including a live demo of programming a Parrot AR.Drone!','Pocket_Code_drone_live_demo_Scratch_2013_conference_w_arrow.jpg','http://goo.gl/PVuBFq'),(34,'Our Apps on the OLPC Tablet','2013-07-17 00:00:00','The preliminary physics version of Pocket Code is the programming app on the new OLPC Tablet!','web-tablet_child_592x400_with_arrow.jpg','http://goo.gl/CWFSv7'),(35,'National Innovation Award','2013-03-07 00:00:00','We were awarded the Austrian National Innovation Award 2013 in the category Multimedia by the Austrian Minister for Economics and Youth Development!','stprmm2013-003_kl.jpg','http://goo.gl/gBW1JF'),(36,'Google Summer of Code 2016','2016-02-29 16:10:02','Google selected us for the fifth time as a Google Summer of Code mentoring organization!','summerofcode.jpg','https://summerofcode.withgoogle.com/organizations/5495166580293632/'),(37,'Alice Game Jam Review','2016-03-10 16:11:40','Congratulations to the winners of the Alice Game Jam!&lt;br/&gt;Find a review, the winners and further information about this Game Jam on our G+ community page!','alicegamejamreview.jpg','https://plus.google.com/+CatrobatOrgAdmin/posts/TsQJsSzrUop');